import { useState, useEfect } from "react";

function useFetch(url) {

    const [data, setData] = useState([]);

    async function fetchUrl() {
        const response = await fetch(url);
        const json = await response.json();

        setData(json);
    }

    useEfect(() => {
        fetchUrl();
    }, []);
    
    useEfect(() => {
        fetchUrl();
    }, [url]);

    return [data];

}

export { useFetch };
